import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [noticias, setNoticias] = useState([]);

  useEffect(() => {
    fetchNoticias();
  }, []);

  async function fetchNoticias() {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/noticias', {
        title: newTitle,
        content: newContent,
      });
      setNewTitle('');
      setNewContent('');
      fetchNoticias();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h1>Notícias</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Título"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
        />
        <textarea
          placeholder="Conteúdo"
          value={newContent}
          onChange={(e) => setNewContent(e.target.value)}
        ></textarea>
        <button type="submit">Adicionar Notícia</button>
      </form>
      {noticias.map((noticia) => (
        <div key={noticia.id}>
          <h3>{noticia.title}</h3>
          <p>{noticia.content}</p>
        </div>
      ))}
    </div>
  );
}

export default App;
