import React, { useState, useEffect } from 'react';
import { Editor, EditorState, convertFromRaw, convertToRaw } from 'draft-js';
import 'draft-js/dist/Draft.css';
import axios from 'axios';

function App() {
  const [noticias, setNoticias] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState(EditorState.createEmpty());
  const [editandoNoticia, setEditandoNoticia] = useState(null);

  useEffect(() => {
    buscarNoticias();
  }, []);

  const buscarNoticias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const criarNoticia = async (e) => {
    e.preventDefault();
    try {
      const contentState = conteudo.getCurrentContent();
      const contentRaw = convertToRaw(contentState);
      await axios.post('http://localhost:5000/noticias', {
        titulo,
        conteudo: contentRaw,
        dataPublicacao: new Date().toISOString(),
      });
      setTitulo('');
      setConteudo(EditorState.createEmpty());
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  const editarNoticia = async (noticia) => {
    setEditandoNoticia(noticia);
    const contentState = convertFromRaw(noticia.conteudo);
    setConteudo(EditorState.createWithContent(contentState));
  };

  const salvarEdicaoNoticia = async () => {
    if (editandoNoticia) {
      const contentState = conteudo.getCurrentContent();
      const contentRaw = convertToRaw(contentState);
      try {
        await axios.put(`http://localhost:5000/noticias/${editandoNoticia.id}`, {
          titulo: editandoNoticia.titulo,
          conteudo: contentRaw,
          dataPublicacao: editandoNoticia.dataPublicacao,
        });
        setEditandoNoticia(null);
        setConteudo(EditorState.createEmpty());
        buscarNoticias();
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h1>Notícias</h1>
      <form onSubmit={criarNoticia}>
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <Editor
          editorState={conteudo}
          onChange={(editorState) => setConteudo(editorState)}
        />
        <button type="submit">Publicar</button>
      </form>
      <ul>
        {noticias.map((noticia) => (
          <li key={noticia.id}>
            <h2>{noticia.titulo}</h2>
            {editandoNoticia === noticia ? (
              <button onClick={salvarEdicaoNoticia}>Salvar</button>
            ) : (
              <button onClick={() => editarNoticia(noticia)}>Editar</button>
            )}
            <Editor
              editorState={EditorState.createWithContent(convertFromRaw(noticia.conteudo))}
              readOnly={true}
              toolbarHidden={true}
            />
            <p>{noticia.dataPublicacao}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
