import React, { useState, useEffect } from 'react';
import { Editor, EditorState } from 'draft-js';
import 'draft-js/dist/Draft.css';
import axios from 'axios';

function App() {
  const [noticias, setNoticias] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState('');
  const [editandoNoticia, setEditandoNoticia] = useState(null);

  useEffect(() => {
    buscarNoticias();
  }, []);

  const buscarNoticias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const criarNoticia = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/noticias', {
        titulo,
        conteudo,
        dataPublicacao: new Date().toISOString(),
      });
      setTitulo('');
      setConteudo('');
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  const editarNoticia = (noticia) => {
    setEditandoNoticia(noticia);
  };

  const cancelarEdicao = () => {
    setEditandoNoticia(null);
  };

  const salvarEdicao = async () => {
    try {
      await axios.put(`http://localhost:5000/noticias/${editandoNoticia.id}`, {
        titulo: editandoNoticia.titulo,
        conteudo: editandoNoticia.conteudo,
        dataPublicacao: editandoNoticia.dataPublicacao,
      });
      setEditandoNoticia(null);
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Notícias</h1>
      <form onSubmit={criarNoticia}>
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <textarea
          placeholder="Conteúdo"
          value={conteudo}
          onChange={(e) => setConteudo(e.target.value)}
        ></textarea>
        <button type="submit">Publicar</button>
      </form>
      <ul>
        {noticias.map((noticia) => (
          <li key={noticia.id}>
            <h2>{noticia.titulo}</h2>
            {editandoNoticia === noticia ? (
              <div>
                <Editor
                  editorState={EditorState.createWithContent(noticia.conteudo)}
                  onChange={(editorState) => {
                    setEditandoNoticia({
                      ...editandoNoticia,
                      conteudo: editorState.getCurrentContent(),
                    });
                  }}
                />
                <button onClick={salvarEdicao}>Salvar</button>
                <button onClick={cancelarEdicao}>Cancelar</button>
              </div>
            ) : (
              <div>
                <Editor
                  editorState={EditorState.createWithContent(noticia.conteudo)}
                  readOnly={true}
                  toolbarHidden={true}
                />
                <p>{noticia.dataPublicacao}</p>
                <button onClick={() => editarNoticia(noticia)}>Editar</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
