import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Editor, EditorState, convertToRaw, convertFromRaw } from 'draft-js';

function App() {
  const [noticias, setNoticias] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  useEffect(() => {
    buscarNoticias();
  }, []);

  const buscarNoticias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const criarNoticia = async (e) => {
    e.preventDefault();
    try {
      const contentState = editorState.getCurrentContent();
      const contentRaw = convertToRaw(contentState);

      await axios.post('http://localhost:5000/noticias', {
        titulo,
        conteudo: JSON.stringify(contentRaw),
        dataPublicacao: new Date().toISOString(),
      });

      setTitulo('');
      setEditorState(EditorState.createEmpty());
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  const handleEditorChange = (newEditorState) => {
    setEditorState(newEditorState);
  };

  return (
    <div>
      <h1>Notícias</h1>
      <form onSubmit={criarNoticia}>
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <Editor editorState={editorState} onChange={handleEditorChange} />
        <button type="submit">Publicar</button>
      </form>
      <ul>
        {noticias.map((noticia) => {
          const contentRaw = JSON.parse(noticia.conteudo);
          const contentState = convertFromRaw(contentRaw);
          const editorState = EditorState.createWithContent(contentState);

          return (
            <li key={noticia.id}>
              <h2>{noticia.titulo}</h2>
              <Editor editorState={editorState} readOnly={true} />
              <p>{noticia.dataPublicacao}</p>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
