import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Editor, EditorState, convertToRaw, convertFromRaw } from 'draft-js';

function App() {
  const [noticias, setNoticias] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState(EditorState.createEmpty());
  const [noticiaId, setNoticiaId] = useState(null);

  useEffect(() => {
    buscarNoticias();
  }, []);

  const buscarNoticias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const criarNoticia = async (e) => {
    e.preventDefault();
    try {
      const conteudoRaw = convertToRaw(conteudo.getCurrentContent());
      await axios.post('http://localhost:5000/noticias', {
        titulo,
        conteudo: conteudoRaw,
        dataPublicacao: new Date().toISOString(),
      });
      setTitulo('');
      setConteudo(EditorState.createEmpty());
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  const editarNoticia = async (noticiaId) => {
    const noticia = noticias.find((item) => item.id === noticiaId);
    if (noticia) {
      const conteudoState = EditorState.createWithContent(
        convertFromRaw(noticia.conteudo)
      );
      setNoticiaId(noticiaId);
      setTitulo(noticia.titulo);
      setConteudo(conteudoState);
    }
  };

  const salvarEdicaoNoticia = async () => {
    try {
      const conteudoRaw = convertToRaw(conteudo.getCurrentContent());
      await axios.put(`http://localhost:5000/noticias/${noticiaId}`, {
        titulo,
        conteudo: conteudoRaw,
      });
      setNoticiaId(null);
      setTitulo('');
      setConteudo(EditorState.createEmpty());
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Notícias</h1>
      <form onSubmit={noticiaId ? salvarEdicaoNoticia : criarNoticia}>
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <Editor
          editorState={conteudo}
          onChange={setConteudo}
        />
        <button type="submit">{noticiaId ? 'Salvar' : 'Publicar'}</button>
      </form>
      <ul>
        {noticias.map((noticia) => (
          <li key={noticia.id}>
            <h2>{noticia.titulo}</h2>
            <Editor
              editorState={EditorState.createWithContent(convertFromRaw(noticia.conteudo))}
              readOnly={true}
            />
            <p>{noticia.dataPublicacao}</p>
            <button onClick={() => editarNoticia(noticia.id)}>Editar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
