import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [noticias, setNoticias] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState('');
  const [noticiaIdEditar, setNoticiaIdEditar] = useState(null);

  useEffect(() => {
    buscarNoticias();
  }, []);

  const buscarNoticias = async () => {
    try {
      const response = await axios.get('http://localhost:5000/noticias');
      setNoticias(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const criarNoticia = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/noticias', {
        titulo,
        conteudo,
        dataPublicacao: new Date().toISOString(),
      });
      setTitulo('');
      setConteudo('');
      buscarNoticias();
    } catch (error) {
      console.error(error);
    }
  };

  const editarNoticia = (noticiaId) => {
    setNoticiaIdEditar(noticiaId);
    const noticia = noticias.find((noticia) => noticia.id === noticiaId);
    if (noticia) {
      setTitulo(noticia.titulo);
      setConteudo(noticia.conteudo);
    }
  };

  const salvarNoticiaEditada = async () => {
    if (noticiaIdEditar) {
      try {
        await axios.put(`http://localhost:5000/noticias/${noticiaIdEditar}`, {
          titulo,
          conteudo,
        });
        setNoticiaIdEditar(null);
        setTitulo('');
        setConteudo('');
        buscarNoticias();
      } catch (error) {
        console.error(error);
      }
    }
  };

  const cancelarEdicao = () => {
    setNoticiaIdEditar(null);
    setTitulo('');
    setConteudo('');
  };

  return (
    <div className="container">
      <h1>Notícias</h1>
      <form onSubmit={criarNoticia}>
        <input
          type="text"
          placeholder="Título"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <textarea
          placeholder="Conteúdo"
          value={conteudo}
          onChange={(e) => setConteudo(e.target.value)}
        ></textarea>
        {noticiaIdEditar ? (
          <div>
            <button type="button" onClick={salvarNoticiaEditada}>Salvar</button>
            <button type="button" onClick={cancelarEdicao}>Cancelar</button>
          </div>
        ) : (
          <button type="submit">Publicar</button>
        )}
      </form>
      <ul className="noticias-list">
        {noticias.map((noticia) => (
          <li className="noticia" key={noticia.id}>
            <h2>{noticia.titulo}</h2>
            <p>{noticia.conteudo}</p>
            <p>{noticia.dataPublicacao}</p>
            {!noticiaIdEditar && (
              <button onClick={() => editarNoticia(noticia.id)}>Editar</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
